import React, { Component } from 'react';
import {
  CardHeader,
  Col,
  Form,
  FormGroup,
  Input,
  ModalFooter,
  ModalBody,
  ModalHeader,
  Modal,
  Button, Label,
} from "reactstrap";
import { ON_CHANGE, SEARCH_DATA, SAVE_DATA, RESET_FORM, UPDATE_DATA, FETCH_COMPLETE, FETCH_DATA, SET_TOTAL_PAGES } from "../../reducer/Action";
import { connect } from 'react-redux'
import { saveCategory, getCategories } from '../../services/CategoriesService';
import swal from 'sweetalert'

class CategoryForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modal: false,
      disabed: false,
    };

    this.toggle = this.toggle.bind(this);
  }

  toggle() {
    this.setState({ modal: !this.state.modal })
  }

  toggleInputText() {
    this.setState({ disabled: !this.state.disabled })
  }

  AlertComponent = async (props) => {
    const { title, icon, text } = props
    const alert = await swal({
      title: title,
      text: text,
      icon: icon,
      buttons: false,
    })
    return alert
  }

  cekInput() {

  }

  handleSubmit = () => {
    const { form, fetchComplete, setTotalPages, fetchData, resetForm } = this.props
    saveCategory(form)
      .then(response => {
        fetchData()
        getCategories(0)
          .then(data => {
            this.toggle()
            fetchComplete(data.content)
            setTotalPages(data.totalPages)
          })
      });
    resetForm();
  }

  render() {
    const { form, inputChange } = this.props;

    return (
      <div className="animated fadeIn">
        <CardHeader>
          <i className="fa fa-align-justify"></i> Data Category
          <div className="card-header-actions">
            <Button className="card-header-action text-dark" color="primary" onClick={this.toggle}>New Category</Button>
          </div>
        </CardHeader>

        <Modal isOpen={this.state.modal} toggle={this.toggle} className={'modal-success ' + this.props.className}>
          <ModalHeader toggle={this.toggle}><strong>New Category</strong></ModalHeader>
          <ModalBody>

            <Form className="form-horizontal">
              <FormGroup row>
                <Col md="4">
                  <Label htmlFor="room-name-input">Category Name</Label>
                </Col>
                <Col xs="12" md="8">
                  <Input type="text"
                    id="category-name-input" name="category-name-input"
                    placeholder="Enter Category Name" value={form.name}
                    onChange={(e) => inputChange('name', e.target.value)}
                  />
                </Col>
              </FormGroup>
            </Form>
          </ModalBody>
          <ModalFooter>
            <Button type="submit" color="primary" onClick={() => this.handleSubmit()}>Save</Button>{' '}
            {/* <Button color="primary" onClick={() => this.toggleInputText()}>Edit</Button>{' '} */}
            <Button color="secondary" onClick={this.toggle}>Cancel</Button>
          </ModalFooter>
        </Modal>
      </div>
    )
  }
}

function mapStateToProps(state) {
  return { ...state };
}

function mapDispatcherToProps(dispatch) {
  return {
    saveData: (payload) => dispatch({ type: SAVE_DATA, payload }),
    updateData: (payload) => dispatch({ type: UPDATE_DATA, payload }),
    inputChange: (inputName, inputValue) => dispatch({ type: ON_CHANGE, payload: { inputName, inputValue } }),
    resetForm: () => dispatch({ type: RESET_FORM }),
    fetchComplete: (payload) => dispatch({ type: FETCH_COMPLETE, payload }),
    setTotalPages: (payload) => dispatch({ type: SET_TOTAL_PAGES, payload }),
    fetchData: () => dispatch({ type: FETCH_DATA }),
  }
}

export default connect(mapStateToProps, mapDispatcherToProps)(CategoryForm);
