import {
  SAVE_DATA,
  UPDATE_DATA,
  DELETE_DATA,
  EDIT_DATA,
  RESET_FORM,
  SEARCH_DATA,
  DETAIL_DATA,
  FETCH_DATA,
  FETCH_COMPLETE,
  ON_CHANGE,
  SET_TOTAL_PAGES,} from './Action'
const defaulFormValue={
  id:'',
  title:'',
  start_bid:'',
  description:'',
  status:'',
  image:'',
  category:{},
};
const initialState = {
  items: [],
  form: { ...defaulFormValue },
  disabled: false,
  isLoading: true,
  totalPages:0,
  seacrh: '',
}

export default function ItemReducer(state = initialState, action) {
  const { type, payload } = action;

      switch (type) {
        case SAVE_DATA:
          return{ ...state, items: state.items.concat([ { ...payload } ])};
        case UPDATE_DATA:
          return{ ...state, items: state.items.map((item)=> item.id === payload.id ? { ...payload } : item)};
        case DELETE_DATA:
          return{ ...state, items: state.items.filter( (item)=> item.id !== payload)};
        case DETAIL_DATA:
          const item = state.items.find((item) => item.id === payload);
          return{ ...state, form: item};
        case RESET_FORM:
          return{ ...state, form: { ...defaulFormValue}};
        case ON_CHANGE:
          const {inputName, inputValue}=payload;
          const form={ ...state.form};
          form[inputName]=inputValue;
          return{ ...state, form: { ...form}};
        case SEARCH_DATA:
          return{ ...state, seacrh: payload};
        case FETCH_COMPLETE:
          return{ ...state, items: [ ...payload], isLoading: false};
        case FETCH_DATA:
          return{ ...state, isLoading: true};
        case SET_TOTAL_PAGES:
          return{ ...state, totalPages:payload};          
        default:
          return{ ...state};
      }
}
