import Axios from "axios"
import { BaseUrl } from "../shared/BaseUrl"
import { myHeaders } from "../shared/MyHeaders"

const getCategories=async(page)=>{
    const { data } = await Axios.get(`${BaseUrl}/categories?page=${page}&size=10`,{
        headers:myHeaders
    })
    console.log('categori',data);

    return data
}

const saveCategory=async(category)=>{
    const response = await Axios.post(`${BaseUrl}/category`, category, {
        headers:myHeaders
    })
    if(response.status===200)return true
}

const deleteCategory=async(id)=>{
    const response = await Axios.delete(`${BaseUrl}/category/${id}`, {
        headers:myHeaders
    })
    if(response.status===200)return true
}

export{
    getCategories,
    saveCategory,
    deleteCategory,
}
