import {
    DETAIL_DATA,
    FETCH_DATA,
    FETCH_COMPLETE,
} from './Action'

const initialState = {
    rooms: [],
    users: [],
    items: [],
    histories: [],
    room: '',
    user: '',
    item: '',
    isLoading: true,

}

export default function DashboardReducer(state = initialState, action) {
    const { type, payload } = action;

    switch (type) {
        case DETAIL_DATA:
            const category = state.categories.find((category) => category.id === payload);
            return { ...state, form: category };
        case 'FETCH_ROOM':
            return { ...state, rooms: [...payload] };
        case 'FETCH_USER':
            return { ...state, users: [...payload] };
        case 'FETCH_ITEM':
            return { ...state, items: [...payload] };
        case 'COUNT_ITEM':
            return { ...state, item: payload };
        case 'COUNT_ROOM':
            return { ...state, room: payload };
        case 'COUNT_USER':
            return { ...state, user: payload };
        case FETCH_COMPLETE:
            return { ...state, histories: [...payload], isLoading: false };
        case FETCH_DATA:
            return { ...state, isLoading: true };
        default:
            return { ...state };
    }
}
