import React, { Component } from 'react';
import {
    Button,
    Spinner,
    CardBody,
    CardHeader,
    Pagination,
    PaginationItem,
    PaginationLink,
    Table,
    ModalHeader, ModalBody, ModalFooter, Modal
} from 'reactstrap';
import { connect } from 'react-redux'
import { getAllDataRoomHistory } from '../../services/RoomHistoryService'
import { DETAIL_DATA, FETCH_DATA, FETCH_COMPLETE } from "../../reducer/Action";


class DashboardTable extends Component {
    constructor(props) {
        super(props);
        this.state = {
            modal: false,
        }
    }

    componentDidMount() {
        this.getData();
    }

    getData = () => {
        const { fetchData, fetchComplete } = this.props;
        fetchData();
        getAllDataRoomHistory().then((response) => {
            console.log(response.data.content);
        })
            .catch(err => {
                console.log(err);
            })
    }

    generateTableRows = () => {
        const { histories, isLoading } = this.props;

        let rows = (
            <tr>
                <td colSpan="6" className="table-warning text-center"><strong><em>Data Still Empty</em></strong></td>
            </tr>
        );

        if (histories.length > 0) {
            rows = <tr><td colSpan="6" className="table-warning text-center"><Spinner color={"danger"}></Spinner></td></tr>
            if (!isLoading) {
                rows = histories.map((history, index) => {
                    return (
                        <tr key={index}>
                            <td>{index + 1}</td>
                            <td><Button color="secondary" onClick={() => this.handleDetail(history.id)}>Detail</Button></td>
                        </tr>
                    )
                });
            };
        }
        return rows;
    }

    render() {
        const { form, totalPages } = this.props;
        return (
            <div className="animated fadeIn">
                <CardHeader>
                    <i className="fa fa-align-justify"></i> Data Item
        </CardHeader>
                <CardBody>
                    <Table hover bordered striped responsive size="sm">
                        <thead>
                            <tr>
                                <th className="text-center">No.</th>
                                <th className="text-center">User</th>
                                <th className="text-center">Address</th>
                                <th className="text-center">Item</th>
                                <th className="text-center">Bid Amount</th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.generateTableRows()}
                        </tbody>
                    </Table>
                </CardBody>

            </div>
        );
    }

}

function mapStateToProps(state) {
    return { ...state };
}

function mapDispatchToProps(dispatch) {
    return {
        fetchData: () => dispatch({ type: FETCH_DATA }),
        fetchComplete: (payload) => dispatch({ type: FETCH_COMPLETE, payload }),
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(DashboardTable);
