import React, { Component } from 'react';
import {
  Button,
  Spinner,
  CardBody,
  Col,
  Table,
  ModalFooter,
  ModalBody,
  ModalHeader,
  Modal,
  Form,
  FormGroup,
  Input,
  Label,
} from 'reactstrap';
import { EDIT_DATA, DETAIL_DATA, FETCH_DATA, FETCH_COMPLETE, DELETE_DATA, SET_TOTAL_PAGES } from "../../reducer/Action";
import { connect } from 'react-redux'
import { getCategories, deleteCategory } from '../../services/CategoriesService';
import PageComponent from '../../component/PageComponent';

class CategoryTable extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modal: false,
      disabed: false,
    }
  }

  componentDidMount() {
    this.getData(0)
  }

  getData = (i) => {
    const { fetchComplete, fetchData, setTotalPages } = this.props;
    fetchData()
    getCategories(i)
      .then(data => {
        fetchComplete(data.content)
        setTotalPages(data.totalPages)
      })
      .catch(err => {
        console.log(err);
      })
  }

  handleDetail = (categoryId) => {
    const { detailData } = this.props;
    detailData(categoryId);
  }

  handleDelete = (categoryId) => {
    const { deleteData } = this.props;
    deleteCategory(categoryId)
      .then(response => {
        if (response) this.getData(0)
      })
  }

  toggleModal = (categoryId) => {
    this.setState({ modal: !this.state.modal })
    this.handleDetail(categoryId)
  }

  toggleInputText() {
    this.setState({ disabled: !this.state.disabled })
  }

  generateTableRows = () => {
    const { categories } = this.props;

    let rows = (
      <tr>
        <td colSpan="3" className="table-warning text-center"><strong><em>Data Still Empty</em></strong></td>
      </tr>
    );

    if (categories.length > 0) {
      rows = <tr><td colSpan="2" className="table-warning text-center"><Spinner color={"danger"}></Spinner></td></tr>
      if (!this.props.isLoading) {
        rows = categories.map((category, index) => {
          return (
            <tr key={index}>
              <td className="text-center">{index + 1}</td>
              <td className="text-center">{category.name}</td>
              <td className="text-center">
                <Button type="button" color="primary" onClick={this.toggleModal(category.id)}>Detail</Button>
                <Button type="button" color="danger" className="shadow" onClick={() => this.handleDelete(category.id)}>Delete</Button>
              </td>
            </tr>
          )
        });
      };
    }
    return rows;
  }

  render() {
    const { form, totalPages, inputChange } = this.props
    return (
      <div className="animated fadeIn">
        <CardBody>
          <Table hover bordered striped responsive size="sm">
            <thead>
              <tr>
                <th className="text-center">No.</th>
                <th className="text-center">Name</th>
                <th className='text-center'>Action</th>
              </tr>
            </thead>
            <tbody>
              {this.generateTableRows()}
            </tbody>
          </Table>
          <PageComponent totalPages={totalPages} getData={this.getData} />
        </CardBody>

        <Modal isOpen={this.state.modal} toggle={this.toggle} className={'modal-success ' + this.props.className}>
          <ModalHeader toggle={this.toggle}><strong>New Category</strong></ModalHeader>
          <ModalBody>
            <Form className="form-horizontal">
              <FormGroup row>
                <Col md="4">
                  <Label htmlFor="room-name-input">Category Name</Label>
                </Col>
                <Col xs="12" md="8">
                  <Input type="text"
                    id="category-name-input" name="category-name-input"
                    placeholder="Enter Category Name" value={form.name}
                    onChange={(e) => inputChange('name', e.target.value)}
                    disabed={this.state.disabed}
                  />
                </Col>
              </FormGroup>
            </Form>
          </ModalBody>
          <ModalFooter>
            <Button type="submit" color="primary" onClick={() => this.handleSubmit()} disabled={this.state.buttonDisable}>Save</Button>{' '}
            <Button color="primary" onClick={() => this.toggleInputText()}>Edit</Button>{' '}
            <Button color="secondary" onClick={this.toggle}>Cancel</Button>
          </ModalFooter>
        </Modal>

      </div>

    );
  }
}

function mapStateToProps(state) {
  return { ...state };
}

function mapDispatchToProps(dispatch) {
  return {
    detailData: (payload) => dispatch({ type: DETAIL_DATA, payload }),
    deleteData: (payload) => dispatch({ type: DELETE_DATA, payload }),
    fetchData: () => dispatch({ type: FETCH_DATA }),
    fetchComplete: (payload) => dispatch({ type: FETCH_COMPLETE, payload }),
    setTotalPages: (payload) => dispatch({ type: SET_TOTAL_PAGES, payload }),
    inputChange: (inputName, inputValue) => dispatch({ type: ON_CHANGE, payload: { inputName, inputValue } }),
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(CategoryTable);
