import { SAVE_DATA,
  UPDATE_DATA,
  DELETE_DATA,
  EDIT_DATA,
  RESET_FORM,
  SEARCH_DATA,
  DETAIL_DATA,
  FETCH_DATA,
  FETCH_COMPLETE,
  ON_CHANGE,
  SET_TOTAL_PAGES,} from './Action'
const defaulFormValue={
  id:'',
  name:'',
};
const initialState={
  categories:[],
  form:{ ...defaulFormValue},
  disabled: false,
  isLoading: true,
  seacrh: '',
  totalPages:0,
}

export default function CategoryReducer(state=initialState,action) {
  const {type, payload}= action;

  switch (type) {
    case SAVE_DATA:
      return{ ...state, categories: state.categories.concat([ { ...payload } ])};
    case UPDATE_DATA:
      return{ ...state, categories: state.categories.map((category)=> category.id === payload.id ? { ...payload } : category)};
    case DELETE_DATA:
      return{ ...state, categories: state.categories.filter( (category)=> category.id !== payload)};
    case DETAIL_DATA:
      const category = state.categories.find((category) => category.id === payload);
      return{ ...state, form: category};
    case RESET_FORM:
      return{ ...state, form: { ...defaulFormValue}};
    case ON_CHANGE:
      const {inputName, inputValue}=payload;
      const form={ ...state.form};
      form[inputName]=inputValue;
      return{ ...state, form: { ...form}};
    case SEARCH_DATA:
      return{ ...state, seacrh: payload};
    case FETCH_COMPLETE:
      return{ ...state, categories: [ ...payload], isLoading: false};
    case FETCH_DATA:
      return{ ...state, isLoading: true};
    case SET_TOTAL_PAGES:
      return{ ...state, totalPages:payload};
    default:
      return{ ...state};
  }
}
