import React, { Component } from 'react';
import {
  Button,
  Spinner,
  CardBody,
  CardHeader,
  Pagination,
  PaginationItem,
  PaginationLink,
  Table,
  ModalHeader, ModalBody, ModalFooter, Modal
} from 'reactstrap';
import { DETAIL_DATA, FETCH_DATA, FETCH_COMPLETE, DELETE_DATA, SET_TOTAL_PAGES } from "../../reducer/Action";
import { connect } from 'react-redux'
import ModalRoom from "../../component/ModalRoom";
import { getRooms } from '../../services/RoomsService';
import PageComponent from '../../component/PageComponent';
import { BrowserRouter as Router, Route, Link, Redirect } from "react-router-dom";

class RoomTable extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modal: false,
    }
  }

  componentDidMount() {
    this.getData(0)
  }

  getData = (i) => {
    const { fetchData, fetchComplete, setTotalPages } = this.props;
    fetchData()
    getRooms(i)
      .then(data => {
        setTotalPages(data.totalPages)
        fetchComplete(data.content)
        console.log(data.content);

      })
      .catch(err => {
        console.log(err);
      })
  }

  goToDetail() {
    const { form } = this.props;
    return <Redirect to={{
      pathname: '/room/detail',
      state: { data: form }
    }}
    />
  }

  handleDetail = (itemId) => {
    const { detailData } = this.props;
    detailData(itemId)
  }

  handleDelete = (itemId) => {
    const { deleteData } = this.props;
  }

  toggleModal = () => {
    this.setState({ modal: !this.state.modal })
  }

  generateTableRows = () => {
    const { rooms, isLoading } = this.props;

    let rows = (
      <tr>
        <td colSpan="9" className="table-warning text-center"><strong><em>Data Still Empty</em></strong></td>
      </tr>
    );

    if (rooms.length > 0) {
      rows = <tr><td colSpan="9" className="table-warning text-center"><Spinner color={"danger"}></Spinner></td></tr>
      if (!isLoading) {
        rows = rooms.map((room, index) => {
          return (
            <tr key={index}>
              <td className="text-center">{index + 1}</td>
              <td>{room.name}</td>
              <td>{room.address}</td>
              <td>{room.item.title}</td>
              <td>{room.user.username}</td>
              <td className="text-center">{room.status}</td>
              <td className="text-center">
                <Link to='{pathname: "/roomdetail",id:"myidddddd"}'><Button size='sm' color=" secondary" onClick={() => this.handleDetail(room.id)}>Detail</Button></Link>
                <Button className="float-right" size='sm' color="danger" onClick={() => this.handleDelete(room.id)}>Delete</Button>
              </td>
            </tr>
          )
        });
      };
    }
    return rows;
  }

  render() {
    const { form, totalPages } = this.props;
    return (
      <div>
        <CardHeader>
          <i className="fa fa-align-justify"></i> Data Room
        </CardHeader>
        <CardBody>
          <Table hover bordered striped responsive size="sm">
            <thead>
              <tr>
                <th className="text-center">No.</th>
                <th className="text-center">Room Number</th>
                <th className="text-center">Address</th>
                <th className="text-center">Item</th>
                <th className="text-center">Auctioneer</th>
                <th className="text-center">Status</th>
                <th className="text-center" colSpan='2' className='text-center' >Action</th>
              </tr>
            </thead>
            <tbody>
              {this.generateTableRows()}
            </tbody>
          </Table>
          <PageComponent totalPages={totalPages} getData={this.getData} />
        </CardBody>

        <Modal isOpen={this.state.modal} toggle={this.toggleModal} className={'modal-info' + this.props.className}>
          <ModalHeader toggle={this.toggleModal}><strong>Room Details</strong></ModalHeader>
          <ModalBody>
            <ModalRoom form={form} />
          </ModalBody>
          <ModalFooter>
            <Button color="secondary" onClick={this.toggleModal}>Close</Button>
          </ModalFooter>
        </Modal>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return { ...state };
}

function mapDispatchToProps(dispatch) {
  return {
    detailData: (payload) => dispatch({ type: DETAIL_DATA, payload }),
    deleteData: (payload) => dispatch({ type: DELETE_DATA, payload }),
    fetchData: () => dispatch({ type: FETCH_DATA }),
    fetchComplete: (payload) => dispatch({ type: FETCH_COMPLETE, payload }),
    setTotalPages: (payload) => dispatch({ type: SET_TOTAL_PAGES, payload }),
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(RoomTable);
