import React, { Component } from 'react';
import {
  Button,
  Spinner,
  CardBody,
  CardHeader,
  Pagination,
  PaginationItem,
  PaginationLink,
  Table,
  ModalHeader, ModalBody, ModalFooter, Modal
} from 'reactstrap';
import { DETAIL_DATA, FETCH_DATA, FETCH_COMPLETE, DELETE_DATA, SET_TOTAL_PAGES } from "../../reducer/Action";
import { connect } from 'react-redux'
import ModalItem from "../../component/ModalItem";
import { getAllItem, deleteDataItem } from '../../services/ItemsService'
import PageComponent from '../../component/PageComponent';

class ItemTable extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modal: false,
    }
  }

  componentDidMount() {
    this.getData();
  }

  getData = () => {
    const { fetchData, fetchComplete, setTotalPages } = this.props;
    fetchData();
    getAllItem().then((response) => {
      console.log(response);
      setTotalPages(response.totalPages)
      fetchComplete(response.content);
    })
      .catch(err => {
        console.log(err);
      })
  }

  handleDetail = (itemId) => {
    const { detailData } = this.props;
    this.toggleModal();
    detailData(itemId);
  }

  handleDelete = (itemId) => {
    const { deleteData } = this.props;
    deleteDataItem(itemId).then((response) => {
      deleteData(itemId);
    })
  }

  toggleModal = () => {
    this.setState({ modal: !this.state.modal })
  }

  generateTableRows = () => {
    const { items, isLoading } = this.props;

    let rows = (
      <tr>
        <td colSpan="6" className="table-warning text-center"><strong><em>Data Still Empty</em></strong></td>
      </tr>
    );

    if (items.length > 0) {
      rows = <tr><td colSpan="6" className="table-warning text-center"><Spinner color={"danger"}></Spinner></td></tr>
      if (!isLoading) {
        rows = items.map((item, index) => {
          return (
            <tr key={index}>
              <td className="text-center">{index + 1}</td>
              <td>{item.title}</td>
              <td className="text-center">{item.startBid}</td>
              <td>{item.description}</td>
              <td className="text-center">{item.status}</td>
              <td className="text-center"><Button color="secondary" onClick={() => this.handleDetail(item.id)}>Detail</Button>

                <Button className="float-right" color="danger" onClick={() => this.handleDelete(item.id)}>Delete</Button>
              </td>
            </tr>
          )
        });
      };
    }
    return rows;
  }

  render() {
    const { form, totalPages } = this.props;
    return (
      <div className="animated fadeIn">
        <CardHeader>
          <i className="fa fa-align-justify"></i> Data Item
        </CardHeader>
        <CardBody>
          <Table hover bordered striped responsive size="sm">
            <thead>
              <tr>
                <th className="text-center">No.</th>
                <th className="text-center">Name item</th>
                <th className="text-center">Start Bid</th>
                <th className="text-center">Description</th>
                <th className="text-center">Status</th>
                <th className="text-center" colSpan="2">Action</th>
              </tr>
            </thead>
            <tbody>
              {this.generateTableRows()}
            </tbody>
          </Table>
          <PageComponent totalPages={totalPages} getData={this.getData} />
        </CardBody>

        <Modal isOpen={this.state.modal} toggle={this.toggleModal} className={'modal-info' + this.props.className}>
          <ModalHeader toggle={this.toggleModal}><strong>Detail Item</strong></ModalHeader>
          <ModalBody>
            <ModalItem form={form} />
          </ModalBody>
          <ModalFooter>
            <Button color="secondary" onClick={this.toggleModal}>Cancel</Button>
          </ModalFooter>
        </Modal>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return { ...state };
}

function mapDispatchToProps(dispatch) {
  return {
    detailData: (payload) => dispatch({ type: DETAIL_DATA, payload }),
    deleteData: (payload) => dispatch({ type: DELETE_DATA, payload }),
    fetchData: () => dispatch({ type: FETCH_DATA }),
    fetchComplete: (payload) => dispatch({ type: FETCH_COMPLETE, payload }),
    setTotalPages: (payload) => dispatch({ type: SET_TOTAL_PAGES, payload }),
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(ItemTable);
