import {
  SAVE_DATA,
  UPDATE_DATA,
  DELETE_DATA,
  RESET_FORM,
  SEARCH_DATA,
  DETAIL_DATA,
  FETCH_DATA,
  FETCH_COMPLETE, ON_CHANGE, HANDLE_PAGE_CHANGE, SET_TOTAL_PAGES
} from './Action'

const defaulFormValue={
  id:'',
  name: '',
  birth_date:'',
  identity_number:'',
  profession:'',
  education:'',
};

const initialState={
  users:[],
  form:{ ...defaulFormValue},
  disabled: false,
  isLoading: false,
  search: '',
  totalPages:0,
}

export default function UserReducer(state=initialState,action) {
  const {type, payload}= action;

  switch (type) {
    case SAVE_DATA:
      return{ ...state, users: state.users.concat([ { ...payload } ])};
    case UPDATE_DATA:
      return{ ...state, users: state.users.map((user)=> user.id === payload.id ? { ...payload } : user)};
    case DELETE_DATA:
      return{ ...state, user: state.users.filter( (user)=> user.id !== payload)};
    case DETAIL_DATA:
      const user = state.users.find((user) => user.id === payload);
      return{ ...state, form: user};
    case RESET_FORM:
      return{ ...state, form: { ...defaulFormValue}};
    case ON_CHANGE:
      const {inputName, inputValue}=payload;
      const form={ ...state.form};
      form[inputName]=inputValue;
      return{ ...state, form: { ...form}};
    case SEARCH_DATA:
      return{ ...state, seacrh: payload};
    case FETCH_COMPLETE:
      return{ ...state, users: [ ...payload], isLoading: false};
    case FETCH_DATA:
      return{ ...state, isLoading: true};
    case SET_TOTAL_PAGES:
      return{ ...state, totalPages:payload}
    default:
      return{ ...state};
  }
}
