import React, { Component } from 'react';
import {
  Button,
  Spinner,
  CardBody,
  CardHeader,
  Pagination,
  PaginationItem,
  PaginationLink,
  Table,
  ModalHeader, ModalBody, ModalFooter, Modal, Col, Label, Input, FormGroup
} from 'reactstrap';
import { DETAIL_DATA, FETCH_DATA, FETCH_COMPLETE, DELETE_DATA, SET_TOTAL_PAGES, HANDLE_PAGE_CHANGE } from "../../reducer/Action";
import { connect } from 'react-redux'
import ModalItem from "../../component/ModalItem";
import { getUsers, getDataUsers } from '../../services/UsersService';
import PageComponent from '../../component/PageComponent';
import ModalUser from '../../component/ModalUser';
import { BrowserRouter as Router, Route, Link, Redirect } from "react-router-dom";

class UserTable extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modal: false,
      number: 0,
      search: '',
      filterUser:[],
      tempUsers:[],
    }
  }

  componentDidMount() {
    this.getData(0)
    this.getDataAll();
  }

  getDataAll=()=>{
    getDataUsers().then(response=>{
      this.setState({ tempUsers: response.content})
    })
  }

  getData = (i) => {
    const { fetchData, fetchComplete, setTotalPages } = this.props;
    fetchData()
    getUsers(i)
      .then(data => {
        fetchComplete(data.content)
        setTotalPages(data.totalPages)
        console.log(data);

      })
      .catch(err => {
        console.log(err);
      })
  }

  handleDetail = (itemId) => {
    const { detailData } = this.props;
    detailData(itemId)
    this.toggleModal()
  }

  handleDelete = (itemId) => {
    const { deleteData } = this.props;
  }

  toggleModal = () => {
    this.setState({ modal: !this.state.modal })
  }

  handleInputSearch=(e)=>{
    if (this.state.search!==null){
      this.setState({ search: e});
    }
    this.handleSearchUser();
  }

  handleSearchUser=()=>{
    if (this.state.search()!==null){
      this.setState({filterUser: this.state.tempUsers.filter(song=>song.title.includes(this.state.seacrh)) });
    }
  }

  generateTableRows = () => {
    const { users, isLoading } = this.props;
    let newArr=users;

    if (this.state.tempUsers.length>0){
      newArr=this.state.tempUsers;
    }

    let rows = (
      <tr>
        <td colSpan="6" className="table-warning text-center"><strong><em>Data Still Empty</em></strong></td>
      </tr>
    );

    if (newArr.length > 0) {
      rows = <tr><td colSpan="6" className="table-warning text-center"><Spinner color={"danger"}></Spinner></td></tr>
      if (!isLoading) {
        rows = newArr.map((user, index) => {
          return (
            <tr key={index}>
              <td className="text-center">{index + 1}</td>
              <td className="text-center">{user.username}</td>
              <td className="text-center">{user.email}</td>
              <td className="text-center">{user.dateRegister}</td>
              <td className="text-center">
                <Link to={{ pathname: "/userdetail", user: user }}><Button size='sm' color="secondary">Detail</Button></Link></td>
              <td className="text-center"><Button size='sm' color="danger" onClick={() => this.handleDelete(user.id)}>Delete</Button></td>
            </tr>
          )
        });
      };
    }
    return rows;
  }

  render() {
    const { form, totalPages } = this.props;
    return (
      <div className="animated fadeIn">
        <CardHeader>
          <i className="icon-people"></i> Data User
          <FormGroup row>
            <Col xs="12" md="9">
              <Input type="text" id="search-input" name="search-input" value={this.state.seacrh} onChange={(e)=> this.handleInputSearch(e.target.value)} />
            </Col>
          </FormGroup>
        </CardHeader>
        <CardBody>
          <Table hover bordered striped responsive size="sm">
            <thead>
              <tr>
                <th className="text-center">No.</th>
                <th className="text-center">Username</th>
                <th className="text-center">Email</th>
                <th className="text-center">Registered</th>
                <th className="text-center" colSpan='2' className="text-center">Action</th>
              </tr>
            </thead>
            <tbody>
              {this.generateTableRows()}
            </tbody>
          </Table>
          <PageComponent totalPages={totalPages} getData={this.getData} />
        </CardBody>

        <Modal isOpen={this.state.modal} toggle={this.toggleModal} className={'modal-info' + this.props.className}>
          <ModalHeader toggle={this.toggleModal}><strong>User Detail</strong></ModalHeader>
          <ModalBody>
            <ModalUser form={form} />
          </ModalBody>
          <ModalFooter>
            <Button color="secondary" onClick={this.toggleModal}>Cancel</Button>
          </ModalFooter>
        </Modal>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return { ...state };
}

function mapDispatchToProps(dispatch) {
  return {
    detailData: (payload) => dispatch({ type: DETAIL_DATA, payload }),
    deleteData: (payload) => dispatch({ type: DELETE_DATA, payload }),
    fetchData: () => dispatch({ type: FETCH_DATA }),
    fetchComplete: (payload) => dispatch({ type: FETCH_COMPLETE, payload }),
    setTotalPages: (payload) => dispatch({ type: SET_TOTAL_PAGES, payload }),
    handlePageChanges: (payload) => dispatch({ type: HANDLE_PAGE_CHANGE, payload }),
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(UserTable);
