import {
  SAVE_DATA,
  UPDATE_DATA,
  DELETE_DATA,
  RESET_FORM,
  SEARCH_DATA,
  DETAIL_DATA,
  FETCH_DATA,
  FETCH_COMPLETE,
  SET_TOTAL_PAGES} from './Action'
const defaulFormValue={
  id:'',
  wallet:{},
  amount:'',
  trxDate:'',
  type:'',
};
const initialState = {
  walletHistories: [],
  form: { ...defaulFormValue },
  disabled: false,
  isLoading: true,
  search: '',
  totalPages:0,
}

export default function WalletHistoryReducer(state = initialState, action) {
  const { type, payload } = action;

  switch (type) {
    case SAVE_DATA:
      return { ...state, walletHistories: state.walletHistories.concat([{ ...payload }]) };
    case UPDATE_DATA:
      return { ...state, walletHistories: state.walletHistories.map((walletHistory) => walletHistory.id === payload ? { ...payload } : walletHistory) };
    case DELETE_DATA:
      return { ...state, walletHistories: state.walletHistories.filter((walletHistory) => walletHistory.id !== payload) };
    case RESET_FORM:
      return { ...state, form: { ...defaulFormValue } };
    case SEARCH_DATA:
      return { ...state, search: payload };
    case DETAIL_DATA:
      const walletHistory = state.walletHistories.find((walletHistory) => walletHistory.id === payload);
      return { ...state, form: walletHistory };
    case FETCH_COMPLETE:
      return { ...state, walletHistories: [...payload], isLoading: false };
    case FETCH_DATA:
      return{ ...state, isLoading: true};
    case SET_TOTAL_PAGES:
      return{ ...state, totalPages: payload}
    default:
      return { ...state };
  }
}
