import React, { Component } from 'react';
import { Button, Spinner, Card, CardBody, CardHeader, Col, Pagination, PaginationItem, PaginationLink, Row, Table } from 'reactstrap';
import { EDIT_DATA, DETAIL_DATA, FETCH_DATA, FETCH_COMPLETE, DELETE_DATA, SET_TOTAL_PAGES } from "../../reducer/Action";
import { connect } from 'react-redux'
import { getCategories, deleteCategory } from '../../services/CategoriesService';
import PageComponent from '../../component/PageComponent';

class CategoryTable extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modal: false,
    }
  }

  componentDidMount() {
    this.getData(0)
  }

  getData = (i) => {
    const { fetchComplete, fetchData, setTotalPages } = this.props;
    fetchData()
    getCategories(i)
      .then(data => {
        fetchComplete(data.content)
        setTotalPages(data.totalPages)
      })
      .catch(err => {
        console.log(err);
      })
  }

  handleDetail = (categoryId) => {
    const { detailData } = this.props;
  }

  handleDelete = (categoryId) => {
    const { deleteData } = this.props;
    deleteCategory(categoryId)
      .then(response => {
        if (response) this.getData(0)
      })
  }

  toggleModal = () => {
    this.setState({ modal: !this.state.modal })
  }

  generateTableRows = () => {
    const { categories } = this.props;

    let rows = (
      <tr>
        <td colSpan="3" className="table-warning text-center"><strong><em>Data Still Empty</em></strong></td>
      </tr>
    );

    if (categories.length > 0) {
      rows = <tr><td colSpan="2" className="table-warning text-center"><Spinner color={"danger"}></Spinner></td></tr>
      if (!this.props.isLoading) {
        rows = categories.map((category, index) => {
          return (
            <tr key={index}>
              <td className="text-center">{index + 1}</td>
              <td className="text-center">{category.name}</td>
              <td className="text-center"><Button type="button" color="danger" className="shadow" onClick={() => this.handleDelete(category.id)}>Delete</Button>
                <Button type="button" color="primary" onClick={this.toggle}>Detail</Button>
              </td>
            </tr>
          )
        });
      };
    }
    return rows;
  }

  render() {
    const { form, totalPages } = this.props
    return (
      <div className="animated fadeIn">
        <CardBody>
          <Table hover bordered striped responsive size="sm">
            <thead>
              <tr>
                <th className="text-center">No.</th>
                <th className="text-center">Name</th>
                <th className='text-center'>Action</th>
              </tr>
            </thead>
            <tbody>
              {this.generateTableRows()}
            </tbody>
          </Table>
          <PageComponent totalPages={totalPages} getData={this.getData} />
        </CardBody>
      </div>

    );
  }
}

function mapStateToProps(state) {
  return { ...state };
}

function mapDispatchToProps(dispatch) {
  return {
    detailData: (payload) => dispatch({ type: DETAIL_DATA, payload }),
    deleteData: (payload) => dispatch({ type: DELETE_DATA, payload }),
    fetchData: () => dispatch({ type: FETCH_DATA }),
    fetchComplete: (payload) => dispatch({ type: FETCH_COMPLETE, payload }),
    setTotalPages: (payload) => dispatch({ type: SET_TOTAL_PAGES, payload }),
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(CategoryTable);
