import React, { Component } from 'react';
import {
  Button,
  Spinner,
  CardBody,
  CardHeader,
  Pagination,
  PaginationItem,
  PaginationLink,
  Table,
  ModalHeader, ModalBody, ModalFooter, Modal
} from 'reactstrap';
import {DETAIL_DATA, FETCH_DATA, FETCH_COMPLETE, DELETE_DATA, SET_TOTAL_PAGES, HANDLE_PAGE_CHANGE} from "../../reducer/Action";
import {connect} from 'react-redux'
import ModalItem from "../../component/ModalItem";
import { getUsers } from '../../services/UsersService';
import PageComponent from '../../component/PageComponent';
import ModalUser from '../../component/ModalUser';

class UserTable extends Component{
  constructor(props) {
    super(props);
    this.state={
      modal: false,
    }
  }

  componentDidMount() {
    this.getData(0)
  }



  getData=(i)=>{
    const {fetchData , fetchComplete, setTotalPages}=this.props;
    fetchData()
    getUsers(i)
    .then(data=>{
      fetchComplete(data.content)
      setTotalPages(data.totalPages)
      console.log(data);

    })
    .catch(err=>{
      console.log(err);
    })
  }

  handleDetail=(itemId)=>{
    const {detailData}=this.props;
    detailData(itemId)
    this.toggleModal()
  }

  handleDelete=(itemId)=>{
    const {deleteData}=this.props;
  }

  toggleModal=()=>{
    this.setState({modal: !this.state.modal})
  }

  generateTableRows=()=>{
    const {users, isLoading}=this.props;

    let rows=(
      <tr>
        <td colSpan="6" className="table-warning text-center"><strong><em>Data Still Empty</em></strong></td>
      </tr>
    );

    if (users.length>0){
      rows=<tr><td colSpan="6" className="table-warning text-center"><Spinner color={"danger"}></Spinner></td></tr>
      if (!isLoading){
        rows=users.map((item, index)=>{
          return(
            <tr key={index}>
              <td>{index+1}</td>
              <td>image</td>
              <td>{item.username}</td>
              <td>{item.email}</td>
              <td>date</td>
              <td><Button size='sm' color="secondary" onClick={()=>this.handleDetail(item.id)}>Detail</Button></td>
              <td><Button size='sm' color="danger" onClick={()=>this.handleDelete(item.id)}>Delete</Button></td>
            </tr>
          )
        });
      };
    }
    return rows;
  }

  render() {
    const {form, totalPages}=this.props;
    return (
      <div className="animated fadeIn">
        <CardHeader>
          <i className="fa fa-align-justify"></i> Data User
        </CardHeader>
        <CardBody>
          <Table hover bordered striped responsive size="sm">
            <thead>
            <tr>
              <th>No.</th>
              <th>Image</th>
              <th>Username</th>
              <th>Email</th>
              <th>Registered</th>
              <th colSpan='2' className="text-center">Action</th>
            </tr>
            </thead>
            <tbody>
            {this.generateTableRows()}
            </tbody>
          </Table>
          <PageComponent totalPages={totalPages} getData={this.getData} />
        </CardBody>

        <Modal isOpen={this.state.modal} toggle={this.toggleModal} className={'modal-info'+ this.props.className}>
          <ModalHeader toggle={this.toggleModal}><strong>User Detail</strong></ModalHeader>
          <ModalBody>
            <ModalUser form={form}/>
          </ModalBody>
          <ModalFooter>
            <Button color="secondary" onClick={this.toggleModal}>Cancel</Button>
          </ModalFooter>
        </Modal>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return{ ...state};
}

function mapDispatchToProps(dispatch) {
  return{
    detailData:(payload)=>dispatch({type: DETAIL_DATA, payload}),
    deleteData:(payload)=>dispatch({type: DELETE_DATA, payload}),
    fetchData:()=>dispatch({type: FETCH_DATA}),
    fetchComplete:(payload)=>dispatch({type: FETCH_COMPLETE, payload}),
    setTotalPages:(payload)=>dispatch({type:SET_TOTAL_PAGES, payload}),
    handlePageChanges:(payload)=>dispatch({type:HANDLE_PAGE_CHANGE, payload}),
  }
}

export default connect(mapStateToProps,mapDispatchToProps)(UserTable);
