import axios from "axios"
import { BaseUrl } from "../shared/BaseUrl"
import { myHeaders } from "../shared/MyHeaders"
import Axios from "axios";

const getUsers = async () => {
  const { data } = await Axios.get(`${BaseUrl}/api/user`)
  return data
}

const getUserById = async (id) => {
    const { data } = await Axios.get(`${BaseUrl}/api/user/${id}`)
    return data
}

const saveUser = async (user) => {
  const response = await Axios.post(`${BaseUrl}/api/user/`,user)
  return response
}

const updateUser = async (user) => {
  console.log(user)
  const response = await Axios.put(`http://localhost:8844/api/user/`,user)
  return response
}

const deleteUserById = async (id) => {
  const response = await Axios.delete(`${BaseUrl}/api/user/${id}`)
  if (response.status === 200) return true
}

export {
  getUsers,
  getUserById,
  saveUser,
  updateUser,
  deleteUserById,
}
