import {
  SAVE_DATA,
  UPDATE_DATA,
  DELETE_DATA,
  RESET_FORM,
  SEARCH_DATA,
  DETAIL_DATA,
  FETCH_DATA,
  FETCH_COMPLETE,
  ON_CHANGE,
  SET_TOTAL_PAGES,
} from './Action'
const defaulFormValue={
  id:'',
  name:'',
  startTime:'',
  endTime:'',
  address:'',
  latitude:'',
  longitude:'',
  online:'',
  status:'',
  type:'',
  item:{},
  user:{},
};
const initialState={
  rooms:[],
  form:{ ...defaulFormValue},
  disabled: false,
  isLoading: true,
  seacrh: '',
  totalPages:0,
}

export default function RoomReducer(state = initialState,action) {
  const {type, payload}=action;

  switch (type) {
    case SAVE_DATA:
      return{ ...state, rooms: state.rooms.concat([ { ...payload } ])};
    case UPDATE_DATA:
      return{ ...state, rooms: state.rooms.map((room)=> room.id === payload.id ? { ...payload } : room )};
    case DELETE_DATA:
      return{ ...state, rooms: state.rooms.filter((room) => room.id !== payload )};
    case DETAIL_DATA:
      const room = state.rooms.find((room)=> room.id === payload);
      return{ ...state, form: room};
    case RESET_FORM:
      return{ ...state, form: { ...defaulFormValue}};
    case ON_CHANGE:
      const {inputName, inputValue}=payload;
      const form={ ...state.form};
      form[inputName]=inputValue;
      return{ ...state, form: { ...form}};
    case SEARCH_DATA:
      return{ ...state, seacrh: payload};
    case FETCH_COMPLETE:
      return{ ...state, rooms: [ ...payload], isLoading: false};
    case FETCH_DATA:
      return{ ...state, isLoading: true};
    case SET_TOTAL_PAGES:
      return{ ...state, totalPages:payload};
    default:
      return{ ...state};
  }
}
